import React, { useEffect, useRef } from 'react';
import {
  Box,
  Container,
  Grid,
  Heading,
  Text,
  VStack,
  useColorModeValue,
} from '@chakra-ui/react';

const HomePage = () => {
  const mapRef = useRef(null);
  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  useEffect(() => {
    // Wait for the map container and required scripts to be loaded
    const initMap = () => {
      // Check if map container exists
      if (!mapRef.current) {
        console.error("Map container not found");
        return;
      }

      // Check if Leaflet is loaded
      if (typeof window.L === 'undefined') {
        console.error("Leaflet is not loaded");
        return;
      }

      try {
        console.log("Initializing map...");

        // Initialize the map with specific options
        const map = window.L.map(mapRef.current, {
          center: [20, 0],
          zoom: 2,
          minZoom: 2,
          maxZoom: 10,
          scrollWheelZoom: true,
          zoomControl: true
        });

        // Add the tile layer
        window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: 'Â© OpenStreetMap contributors'
        }).addTo(map);

        // Create layer groups
        const institutesLayer = window.L.layerGroup().addTo(map);
        const redTeamingLayer = window.L.layerGroup().addTo(map);
        const barriersLayer = window.L.layerGroup().addTo(map);
        const fundingLayer = window.L.layerGroup().addTo(map);

        // Store layers in window for access by external scripts
        window.map = map;
        window.institutesLayer = institutesLayer;
        window.redTeamingLayer = redTeamingLayer;
        window.barriersLayer = barriersLayer;
        window.fundingLayer = fundingLayer;

        // Initialize all layers if the functions are available
        if (typeof window.createInstituteMarkers === 'function') {
          window.createInstituteMarkers();
        }
        if (typeof window.createRedTeamingHeatmap === 'function') {
          window.createRedTeamingHeatmap();
        }
        if (typeof window.createBarriersOverlay === 'function') {
          window.createBarriersOverlay();
        }
        if (typeof window.createFundingHeatmap === 'function') {
          window.createFundingHeatmap();
        }

        // Add layer controls
        const overlayMaps = {
          "AI Safety Institutes": institutesLayer,
          "Red Teaming Resources": redTeamingLayer,
          "Barriers to Participation": barriersLayer,
          "Funding & Investment": fundingLayer
        };

        window.L.control.layers(null, overlayMaps, {
          collapsed: false,
          position: 'topright'
        }).addTo(map);

        // Force a resize event to ensure the map renders correctly
        setTimeout(() => {
          map.invalidateSize();
        }, 100);

        console.log("Map initialization complete");
      } catch (error) {
        console.error("Error initializing map:", error);
      }
    };

    // Start map initialization
    const timer = setInterval(() => {
      if (typeof window.L !== 'undefined' && 
          window.institutesData && 
          window.redTeamingData && 
          window.barriersData && 
          window.fundingData) {
        clearInterval(timer);
        initMap();
      }
    }, 100);

    // Cleanup function
    return () => {
      clearInterval(timer);
      if (window.map) {
        window.map.remove();
        window.map = null;
      }
    };
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <Box as="main" bg={bgColor} minH="100vh">
      <Container maxW="container.xl" p={4}>
        {/* Section 1: Introduction */}
        <Box as="section" id="intro" mb={8}>
          <Heading as="h2" size="xl" mb={6}>
            The Challenge: An Uneven AI Landscape
          </Heading>
          <VStack spacing={4} align="stretch">
            <Text fontSize="lg">
              Artificial Intelligence holds immense promise, but its development and
              deployment reveal a significant global imbalance. Primarily engineered
              in the Global North, AI systems increasingly impact diverse
              populations worldwide, yet the governance frameworks meant to ensure
              safety and fairness often lack crucial representation from the Global
              Majority.
            </Text>
            <Text fontSize="lg">
              This disconnect isn't just theoretical; it carries tangible risks.
              Without inclusive perspectives during development and testing (like AI
              red teaming), systems can perpetuate linguistic biases, overlook
              unique localized harms, and fail to account for culturally specific
              contexts. Access to vital safety evaluation practices, such as red
              teaming, is often restricted for independent researchers globally due
              to proprietary models, restrictive terms of service, and resource
              constraints, further limiting the diversity of evaluation.
            </Text>
            <Text fontSize="lg">
              This project aims to map this fragmented global landscape of AI
              governance and safety initiatives. By visualizing where resources,
              regulations, barriers, and expertise are concentrated (and where they
              are lacking), we provide a clearer foundation for analyzing the
              current ecosystem and advocating for more coherent, inclusive
              international governance frameworks.
            </Text>
          </VStack>
        </Box>

        {/* Section 2: Map Visualization */}
        <Box as="section" id="map-visualization" mb={8}>
          <Heading as="h2" size="xl" mb={6}>
            Interactive Map: Global AI Governance & Safety Ecosystem
          </Heading>

          {/* Map Container */}
          <Box
            ref={mapRef}
            id="map"
            h={{ base: "50vh", md: "70vh" }}
            w="100%"
            border="1px"
            borderColor={borderColor}
            borderRadius="lg"
            overflow="hidden"
            position="relative"
            mb={8}
          />

          {/* Map Legend */}
          <Box
            p={4}
            bg={useColorModeValue('gray.50', 'gray.700')}
            borderRadius="lg"
            border="1px"
            borderColor={borderColor}
          >
            <VStack spacing={4} align="stretch">
              <Heading as="h3" size="md">
                Map Layers
              </Heading>
              <VStack spacing={2} align="stretch">
                <Text>
                  <strong>ðï¸ AI Safety Institutes:</strong> National and supranational bodies
                </Text>
                <Text>
                  <strong>ð Red Teaming Resources:</strong> Tools, conferences, research hubs, and bug bounties
                </Text>
                <Text>
                  <strong>ð Barriers to Participation:</strong> Legal risks and resource access by region
                </Text>
                <Text>
                  <strong>ð° Funding & Investment:</strong> Government, VC, and philanthropic sources
                </Text>
              </VStack>
            </VStack>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default HomePage;
