import React, { useEffect, useRef } from 'react';
import {
  Box,
  Container,
  Heading,
  Text,
  VStack,
  useColorModeValue,
} from '@chakra-ui/react';

const HomePage = () => {
  const mapRef = useRef(null);
  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  useEffect(() => {
    const initializeMap = () => {
      if (!mapRef.current || !window.L) return;

      try {
        // Initialize map
        const map = window.L.map(mapRef.current, {
          center: [20, 0],
          zoom: 2,
          minZoom: 2,
          maxZoom: 10
        });

        // Add tile layer
        window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '¬© OpenStreetMap contributors'
        }).addTo(map);

        // Create layer groups
        const institutesLayer = window.L.layerGroup();
        const redTeamingLayer = window.L.layerGroup();
        const barriersLayer = window.L.layerGroup();
        const fundingLayer = window.L.layerGroup();

        // Add layers to map
        institutesLayer.addTo(map);
        redTeamingLayer.addTo(map);
        barriersLayer.addTo(map);
        fundingLayer.addTo(map);

        // Create markers for institutes
        if (window.institutesData) {
          window.institutesData.forEach(institute => {
            const marker = window.L.marker([institute.latitude, institute.longitude], {
              icon: window.L.divIcon({
                className: 'institute-marker',
                html: '<div style="background-color: #800080; width: 16px; height: 16px; border-radius: 50%; border: 2px solid white; display: flex; justify-content: center; align-items: center;"><span style="color: white; font-size: 12px;">üèõÔ∏è</span></div>',
                iconSize: [20, 20]
              })
            });
            marker.bindPopup(`
              <strong>${institute.name}</strong><br>
              Type: ${institute.type}<br>
              Location: ${institute.location_primary}
              ${institute.notes ? `<br>Notes: ${institute.notes}` : ''}
            `);
            institutesLayer.addLayer(marker);
          });
        }

        // Create heatmap for red teaming resources
        if (window.redTeamingData && window.L.heatLayer) {
          const points = window.redTeamingData.map(point => [
            point.latitude,
            point.longitude,
            1
          ]);
          const heatmap = window.L.heatLayer(points, {
            radius: 30,
            blur: 20,
            maxZoom: 10,
            gradient: {0.4: '#ADD8E6', 0.6: '#4169E1', 0.8: '#0000CD', 1.0: '#00008B'}
          });
          redTeamingLayer.addLayer(heatmap);
        }

        // Create barriers overlay
        if (window.barriersData) {
          window.barriersData.forEach(region => {
            // Add your barriers visualization here
            // This will depend on your GeoJSON data
          });
        }

        // Create funding heatmap
        if (window.fundingData && window.L.heatLayer) {
          const points = window.fundingData
            .filter(point => point.amount_usd)
            .map(point => [
              point.latitude,
              point.longitude,
              Math.log10(point.amount_usd || 1000000)
            ]);
          const heatmap = window.L.heatLayer(points, {
            radius: 35,
            blur: 25,
            maxZoom: 10,
            gradient: {0.4: '#90EE90', 0.6: '#32CD32', 0.8: '#228B22', 1.0: '#006400'}
          });
          fundingLayer.addLayer(heatmap);
        }

        // Add layer controls
        const overlayMaps = {
          "AI Safety Institutes": institutesLayer,
          "Red Teaming Resources": redTeamingLayer,
          "Barriers to Participation": barriersLayer,
          "Funding & Investment": fundingLayer
        };

        window.L.control.layers(null, overlayMaps, {
          collapsed: false,
          position: 'topright'
        }).addTo(map);

        // Store map instance for cleanup
        mapRef.current._leaflet = map;

        // Force a resize event
        setTimeout(() => {
          map.invalidateSize();
        }, 100);

      } catch (error) {
        console.error('Error initializing map:', error);
      }
    };

    // Try to initialize map immediately if Leaflet is loaded
    if (window.L) {
      initializeMap();
    } else {
      // If Leaflet isn't loaded yet, wait for it
      const checkLeaflet = setInterval(() => {
        if (window.L) {
          clearInterval(checkLeaflet);
          initializeMap();
        }
      }, 100);

      return () => clearInterval(checkLeaflet);
    }

    // Cleanup
    return () => {
      if (mapRef.current && mapRef.current._leaflet) {
        mapRef.current._leaflet.remove();
      }
    };
  }, []);

  return (
    <Box as="main" bg={bgColor} minH="100vh" w="100%">
      <Container maxW="container.xl" py={8}>
        {/* Introduction */}
        <VStack spacing={8} align="stretch" mb={8}>
          <Heading as="h1" size="2xl">
            Global AI Governance & Safety Ecosystem
          </Heading>
          <Text fontSize="xl">
            Mapping the global landscape of AI safety initiatives, resources, and barriers to participation.
          </Text>
        </VStack>

        {/* Map Container */}
        <Box
          ref={mapRef}
          h="70vh"
          minH="400px"
          w="100%"
          borderRadius="lg"
          overflow="hidden"
          border="1px solid"
          borderColor={borderColor}
          mb={8}
        />

        {/* Legend */}
        <Box p={4} bg={useColorModeValue('gray.50', 'gray.700')} borderRadius="lg">
          <VStack spacing={4} align="stretch">
            <Heading as="h3" size="md">Map Layers</Heading>
            <VStack spacing={2} align="stretch">
              <Text>üèõÔ∏è <strong>AI Safety Institutes:</strong> National and supranational bodies</Text>
              <Text>üîç <strong>Red Teaming Resources:</strong> Tools, conferences, research hubs, and bug bounties</Text>
              <Text>üåç <strong>Barriers to Participation:</strong> Legal risks and resource access by region</Text>
              <Text>üí∞ <strong>Funding & Investment:</strong> Government, VC, and philanthropic sources</Text>
            </VStack>
          </VStack>
        </Box>
      </Container>
    </Box>
  );
};

export default HomePage;
