import React, { useEffect, useRef } from 'react';
import {
  Box,
  Container,
  Grid,
  Heading,
  Text,
  VStack,
  useColorModeValue,
} from '@chakra-ui/react';

const HomePage = () => {
  const mapRef = useRef(null);
  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  useEffect(() => {
    // Wait for the map container and required scripts to be loaded
    const initMap = () => {
      // Check if map container exists
      if (!mapRef.current) {
        console.error("Map container not found");
        return;
      }

      // Check if Leaflet is loaded
      if (typeof window.L === 'undefined') {
        console.error("Leaflet is not loaded");
        return;
      }

      // Check if data is loaded
      if (!window.institutesData || !window.redTeamingData || !window.barriersData || !window.fundingData) {
        console.log("Waiting for data to load...");
        setTimeout(initMap, 100);
        return;
      }

      // Initialize map if not already initialized
      if (!window.map) {
        try {
          console.log("Initializing map...");

          // Initialize the map
          window.map = window.L.map(mapRef.current).setView([20, 0], 2);

          // Add the tile layer
          window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '© OpenStreetMap contributors'
          }).addTo(window.map);

          // Create layer groups
          window.institutesLayer = window.L.layerGroup().addTo(window.map);
          window.redTeamingLayer = window.L.layerGroup().addTo(window.map);
          window.barriersLayer = window.L.layerGroup().addTo(window.map);
          window.fundingLayer = window.L.layerGroup().addTo(window.map);

          // Initialize all layers
          if (typeof window.createInstituteMarkers === 'function') {
            window.createInstituteMarkers();
          }
          if (typeof window.createRedTeamingHeatmap === 'function') {
            window.createRedTeamingHeatmap();
          }
          if (typeof window.createBarriersOverlay === 'function') {
            window.createBarriersOverlay();
          }
          if (typeof window.createFundingHeatmap === 'function') {
            window.createFundingHeatmap();
          }

          // Add layer controls
          const overlayMaps = {
            "AI Safety Institutes": window.institutesLayer,
            "Red Teaming Resources": window.redTeamingLayer,
            "Barriers to Participation": window.barriersLayer,
            "Funding & Investment": window.fundingLayer
          };

          window.L.control.layers(null, overlayMaps).addTo(window.map);

          console.log("Map initialization complete");
        } catch (error) {
          console.error("Error initializing map:", error);
        }
      }
    };

    // Start map initialization
    initMap();

    // Cleanup function
    return () => {
      if (window.map) {
        window.map.remove();
        window.map = null;
      }
    };
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <Box as="main" bg={bgColor} minH="100vh">
      <Container maxW="container.xl" p={8}>
        {/* Section 1: Introduction */}
        <Box as="section" id="intro" mb={12}>
          <Heading as="h2" size="xl" mb={6}>
            The Challenge: An Uneven AI Landscape
          </Heading>
          <VStack spacing={4} align="stretch">
            <Text fontSize="lg">
              Artificial Intelligence holds immense promise, but its development and
              deployment reveal a significant global imbalance. Primarily engineered
              in the Global North, AI systems increasingly impact diverse
              populations worldwide, yet the governance frameworks meant to ensure
              safety and fairness often lack crucial representation from the Global
              Majority.
            </Text>
            <Text fontSize="lg">
              This disconnect isn't just theoretical; it carries tangible risks.
              Without inclusive perspectives during development and testing (like AI
              red teaming), systems can perpetuate linguistic biases, overlook
              unique localized harms, and fail to account for culturally specific
              contexts. Access to vital safety evaluation practices, such as red
              teaming, is often restricted for independent researchers globally due
              to proprietary models, restrictive terms of service, and resource
              constraints, further limiting the diversity of evaluation.
            </Text>
            <Text fontSize="lg">
              This project aims to map this fragmented global landscape of AI
              governance and safety initiatives. By visualizing where resources,
              regulations, barriers, and expertise are concentrated (and where they
              are lacking), we provide a clearer foundation for analyzing the
              current ecosystem and advocating for more coherent, inclusive
              international governance frameworks.
            </Text>
          </VStack>
        </Box>

        {/* Section 2: Map Visualization */}
        <Box as="section" id="map-visualization" mb={12}>
          <Heading as="h2" size="xl" mb={6}>
            Interactive Map: Global AI Governance & Safety Ecosystem
          </Heading>
          <Grid
            templateColumns={{ base: '1fr', lg: '2fr 1fr' }}
            gap={8}
            alignItems="start"
          >
            {/* Map Container */}
            <Box
              ref={mapRef}
              id="map"
              h="600px"
              border="1px"
              borderColor={borderColor}
              borderRadius="lg"
              overflow="hidden"
              position="relative"
            />

            {/* Map Explainer */}
            <Box
              as="aside"
              className="map-explainer"
              p={6}
              bg={useColorModeValue('gray.50', 'gray.700')}
              borderRadius="lg"
              border="1px"
              borderColor={borderColor}
            >
              <VStack spacing={4} align="stretch">
                <Heading as="h3" size="lg" mb={2}>
                  Map Details
                </Heading>

                <Box>
                  <Text fontWeight="bold" mb={2}>
                    Layers & Data
                  </Text>
                  <Text>
                    The map intends to display several layers of information, which
                    can be toggled using the controls:
                  </Text>
                  <VStack spacing={2} align="stretch" mt={2} pl={4}>
                    <Text>
                      <strong>AI Safety Institutes:</strong> Locations of key
                      national and supranational bodies (e.g., UK AISI, US AISI, EU
                      AI Office).
                    </Text>
                    <Text>
                      <strong>Red Teaming Resources:</strong> Locations of relevant
                      resources like open-source tools, conferences, research hubs,
                      and bug bounty programs (Displayed as clustered markers).
                    </Text>
                    <Text>
                      <strong>Barriers to Participation:</strong> Regional overlays
                      indicating potential legal risks and resource access
                      challenges (Displayed as a choropleth map).
                    </Text>
                    <Text>
                      <strong>Funding & Investment:</strong> Locations of major
                      funding programs, VC investments, and philanthropic efforts
                      (Displayed as clustered markers).
                    </Text>
                  </VStack>
                </Box>

                <Box>
                  <Text fontWeight="bold" mb={2}>
                    Interactivity
                  </Text>
                  <Text>
                    <strong>Zoom/Pan:</strong> Explore different regions.{' '}
                    <strong>Layer Controls:</strong> Use the checkboxes (top right)
                    to show or hide layers. <strong>Click/Hover:</strong> Click on
                    points (Institutes, Resources, Funding) or colored regions
                    (Barriers) to view details in popups.
                  </Text>
                </Box>

                <Box>
                  <Text fontWeight="bold" mb={2}>
                    Purpose
                  </Text>
                  <Text>
                    This visualization aims to provide an empirical grounding for
                    understanding the current state of AI safety and governance. By
                    mapping these elements, we can better identify geographic
                    disparities, resource gaps, and areas where regulatory or
                    support structures are concentrated or lacking, informing the
                    discussion around building more globally inclusive AI
                    ecosystems.
                  </Text>
                </Box>
              </VStack>
            </Box>
          </Grid>
        </Box>
      </Container>
    </Box>
  );
};

export default HomePage;
