sourcesContent:
import React, { useEffect, useRef, useState } from 'react';
import {
  Box,
  Container,
  Tabs,
  TabList,
  Tab,
  TabPanels,
  TabPanel,
  Heading,
  Text,
  Link,
  VStack,
  UnorderedList,
  ListItem,
  useColorModeValue,
  HStack,
  Select,
  Checkbox,
  RadioGroup,
  Radio,
  Stack,
} from '@chakra-ui/react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Complete data constants with coordinates
const countryCoordinates = {
  USA: [37.0902, -95.7129],
  CHN: [35.8617, 104.1954],
  GBR: [55.3781, -3.4360],
  DEU: [51.1657, 10.4515],
  FRA: [46.2276, 2.2137],
  JPN: [36.2048, 138.2529],
  IND: [20.5937, 78.9629],
  CAN: [56.1304, -106.3468],
  KOR: [35.9078, 127.7669],
  AUS: [-25.2744, 133.7751],
  IDN: [-0.7893, 113.9213],
  PAK: [30.3753, 69.3451],
  BRA: [-14.2350, -51.9253],
  NGA: [9.0820, 8.6753],
  BGD: [23.6850, 90.3563],
  RUS: [61.5240, 105.3188],
  MEX: [23.6345, -102.5528]
};

// Readiness data
const readinessData = [
  { countryName: "United States", iso_a3: "USA", readinessScore: 87.03 },
  { countryName: "Singapore", iso_a3: "SGP", readinessScore: 84.25 },
  { countryName: "United Kingdom", iso_a3: "GBR", readinessScore: 78.88 },
  { countryName: "Canada", iso_a3: "CAN", readinessScore: 78.18 },
  { countryName: "Netherlands", iso_a3: "NLD", readinessScore: 77.23 },
  { countryName: "Germany", iso_a3: "DEU", readinessScore: 76.90 },
  { countryName: "Finland", iso_a3: "FIN", readinessScore: 76.48 },
  { countryName: "Australia", iso_a3: "AUS", readinessScore: 76.45 },
  { countryName: "France", iso_a3: "FRA", readinessScore: 79.36 },
  { countryName: "South Korea", iso_a3: "KOR", readinessScore: 79.98 },
  { countryName: "Japan", iso_a3: "JPN", readinessScore: 75.75 },
  { countryName: "Sweden", iso_a3: "SWE", readinessScore: 75.40 },
  { countryName: "Denmark", iso_a3: "DNK", readinessScore: 74.71 },
  { countryName: "Israel", iso_a3: "ISR", readinessScore: 74.52 },
  { countryName: "Taiwan", iso_a3: "TWN", readinessScore: 74.58 },
  { countryName: "Ireland", iso_a3: "IRL", readinessScore: 73.18 },
  { countryName: "Austria", iso_a3: "AUT", readinessScore: 72.84 },
  { countryName: "Saudi Arabia", iso_a3: "SAU", readinessScore: 72.36 },
  { countryName: "China", iso_a3: "CHN", readinessScore: 72.01 },
  { countryName: "Estonia", iso_a3: "EST", readinessScore: 72.62 },
  { countryName: "Malaysia", iso_a3: "MYS", readinessScore: 71.40 },
  { countryName: "Italy", iso_a3: "ITA", readinessScore: 71.22 },
  { countryName: "Portugal", iso_a3: "PRT", readinessScore: 70.93 },
  { countryName: "Luxembourg", iso_a3: "LUX", readinessScore: 70.63 },
  { countryName: "Belgium", iso_a3: "BEL", readinessScore: 72.69 },
  { countryName: "Czech Republic", iso_a3: "CZE", readinessScore: 70.23 },
  { countryName: "Spain", iso_a3: "ESP", readinessScore: 69.25 },
  { countryName: "Switzerland", iso_a3: "CHE", readinessScore: 69.42 },
  { countryName: "Iceland", iso_a3: "ISL", readinessScore: 69.82 },
  { countryName: "Qatar", iso_a3: "QAT", readinessScore: 68.22 }
];

// Bills/Legislative data
const aiBillsData = [
  { countryName: "Madagascar", iso_a3: "MDG", mentions: 0 },
  { countryName: "Sri Lanka", iso_a3: "LKA", mentions: 0 },
  { countryName: "Tanzania", iso_a3: "TZA", mentions: 0 },
  { countryName: "Sierra Leone", iso_a3: "SLE", mentions: 0 },
  { countryName: "Seychelles", iso_a3: "SYC", mentions: 0 },
  { countryName: "Papua New Guinea", iso_a3: "PNG", mentions: 0 },
  { countryName: "Azerbaijan", iso_a3: "AZE", mentions: 0 },
  { countryName: "Armenia", iso_a3: "ARM", mentions: 0 },
  { countryName: "Luxembourg", iso_a3: "LUX", mentions: 0 },
  { countryName: "Lesotho", iso_a3: "LSO", mentions: 0 },
  { countryName: "Czech Republic", iso_a3: "CZE", mentions: 0 },
  { countryName: "Barbados", iso_a3: "BRB", mentions: 0 },
  { countryName: "Maldives", iso_a3: "MDV", mentions: 0 },
  { countryName: "Malta", iso_a3: "MLT", mentions: 1 },
  { countryName: "El Salvador", iso_a3: "SLV", mentions: 1 },
  { countryName: "Zambia", iso_a3: "ZMB", mentions: 1 },
  { countryName: "China", iso_a3: "CHN", mentions: 1 },
  { countryName: "Ukraine", iso_a3: "UKR", mentions: 2 },
  { countryName: "Latvia", iso_a3: "LVA", mentions: 2 },
  { countryName: "Portugal", iso_a3: "PRT", mentions: 3 },
  { countryName: "Germany", iso_a3: "DEU", mentions: 3 },
  { countryName: "Zimbabwe", iso_a3: "ZWE", mentions: 4 },
  { countryName: "Dominican Republic", iso_a3: "DOM", mentions: 4 },
  { countryName: "Philippines", iso_a3: "PHL", mentions: 5 },
  { countryName: "Moldova", iso_a3: "MDA", mentions: 5 },
  { countryName: "Trinidad and Tobago", iso_a3: "TTO", mentions: 5 },
  { countryName: "Belgium", iso_a3: "BEL", mentions: 5 },
  { countryName: "Panama", iso_a3: "PAN", mentions: 5 },
  { countryName: "Slovenia", iso_a3: "SVN", mentions: 6 },
  { countryName: "Liechtenstein", iso_a3: "LIE", mentions: 7 },
  { countryName: "Fiji", iso_a3: "FJI", mentions: 8 },
  { countryName: "Russia", iso_a3: "RUS", mentions: 8 },
  { countryName: "South Korea", iso_a3: "KOR", mentions: 10 },
  { countryName: "Romania", iso_a3: "ROU", mentions: 11 },
  { countryName: "Mauritius", iso_a3: "MUS", mentions: 11 },
  { countryName: "Pakistan", iso_a3: "PAK", mentions: 11 },
  { countryName: "New Zealand", iso_a3: "NZL", mentions: 12 },
  { countryName: "Poland", iso_a3: "POL", mentions: 13 },
  { countryName: "Uruguay", iso_a3: "URY", mentions: 17 },
  { countryName: "India", iso_a3: "IND", mentions: 17 },
  { countryName: "Ecuador", iso_a3: "ECU", mentions: 18 }
];

// Investment and Population data
const investmentData = [
  { countryName: "United States", iso_a3: "USA", investment: 328548 },
  { countryName: "China", iso_a3: "CHN", investment: 132665 },
  { countryName: "India", iso_a3: "IND", investment: 16147 },
  { countryName: "United Kingdom", iso_a3: "GBR", investment: 25541 },
  { countryName: "Germany", iso_a3: "DEU", investment: 14300 },
  { countryName: "France", iso_a3: "FRA", investment: 10185 },
  { countryName: "Canada", iso_a3: "CAN", investment: 12457 },
  { countryName: "South Korea", iso_a3: "KOR", investment: 10348 },
  { countryName: "Singapore", iso_a3: "SGP", investment: 7005 },
  { countryName: "Sweden", iso_a3: "SWE", investment: 8281 }
];

// Population data
const populationData = [
  { countryName: "China", iso_a3: "CHN", population: 1441 },
  { countryName: "India", iso_a3: "IND", population: 1380 },
  { countryName: "United States", iso_a3: "USA", population: 331 },
  { countryName: "Indonesia", iso_a3: "IDN", population: 273 },
  { countryName: "Pakistan", iso_a3: "PAK", population: 225 },
  { countryName: "Brazil", iso_a3: "BRA", population: 213 },
  { countryName: "Nigeria", iso_a3: "NGA", population: 211 },
  { countryName: "Bangladesh", iso_a3: "BGD", population: 166 },
  { countryName: "Russia", iso_a3: "RUS", population: 146 },
  { countryName: "Mexico", iso_a3: "MEX", population: 128 },
  { countryName: "Japan", iso_a3: "JPN", population: 126 },
  { countryName: "Philippines", iso_a3: "PHL", population: 109 },
  { countryName: "Egypt", iso_a3: "EGY", population: 104 },
  { countryName: "Vietnam", iso_a3: "VNM", population: 98 },
  { countryName: "Democratic Republic of the Congo", iso_a3: "COD", population: 89 },
  { countryName: "Turkey", iso_a3: "TUR", population: 84 },
  { countryName: "Iran", iso_a3: "IRN", population: 83 },
  { countryName: "Germany", iso_a3: "DEU", population: 83 },
  { countryName: "Thailand", iso_a3: "THA", population: 70 },
  { countryName: "United Kingdom", iso_a3: "GBR", population: 67 },
  { countryName: "France", iso_a3: "FRA", population: 67 },
  { countryName: "Tanzania", iso_a3: "TZA", population: 61 },
  { countryName: "Italy", iso_a3: "ITA", population: 60 },
  { countryName: "South Africa", iso_a3: "ZAF", population: 60 }
];

// Helper functions
const getReadinessColor = (score) => {
  if (score === null || score === undefined) return '#CCCCCC';
  const normalized = Math.max(0, Math.min(1, score / 100));
  if (normalized < 0.5) {
    // Red to Yellow
    return `rgb(255, ${Math.round(255 * (normalized * 2))}, 0)`;
  } else {
    // Yellow to Green
    return `rgb(${Math.round(255 * (2 - normalized * 2))}, 255, 0)`;
  }
};

const getBillsColor = (mentions) => {
  if (mentions === null || mentions === undefined) return '#CCCCCC';
  if (mentions === 0) return '#EFF3FF';
  if (mentions <= 5) return '#C6DBEF';
  if (mentions <= 10) return '#9ECAE1';
  if (mentions <= 15) return '#6BAED6';
  if (mentions <= 20) return '#3182BD';
  return '#08519C';
};

const getInvestmentColor = (investment) => {
  if (!investment) return '#CCCCCC';
  if (investment >= 100000) return '#00C851';
  if (investment >= 50000) return '#33B679';
  if (investment >= 10000) return '#66A4A1';
  if (investment >= 5000) return '#9991C9';
  if (investment >= 1000) return '#CC7EF1';
  return '#FF6BF1';
};

const getPopulationRadius = (population) => {
  if (!population) return 0;
  return Math.sqrt(population) * 0.3;
};

const TabContent = () => {
  const mapRef = useRef(null);
  const [selectedMetric, setSelectedMetric] = useState('Readiness');
  const [showPopulation, setShowPopulation] = useState(false);
  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  useEffect(() => {
    // Ensure the map container exists and is mounted
    if (!mapRef.current) return;

    // Create a variable to store the map instance
    let map = null;

    // Small delay to ensure DOM is ready
    const initMap = setTimeout(() => {
      try {
        // Initialize map
        map = L.map(mapRef.current, {
          center: [20, 0],
          zoom: 2,
          minZoom: 2,
          maxZoom: 6
        });

        // Add tile layer
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '© OpenStreetMap contributors'
        }).addTo(map);

        // Create lookup maps for efficient data access
        const readinessLookup = {};
        const billsLookup = {};
        const investmentLookup = {};

        readinessData.forEach(item => {
          readinessLookup[item.iso_a3] = item.readinessScore;
        });

        aiBillsData.forEach(item => {
          billsLookup[item.iso_a3] = item.mentions;
        });

        investmentData.forEach(item => {
          investmentLookup[item.iso_a3] = item.investment;
        });

        // Fetch and add GeoJSON layer
        fetch('https://raw.githubusercontent.com/holtzy/D3-graph-gallery/master/DATA/world.geojson')
          .then(response => response.json())
          .then(data => {
            // Create and add the GeoJSON layer
            const geojsonLayer = L.geoJSON(data, {
              style: (feature) => {
                const countryCode = feature.properties.iso_a3;
                let fillColor = '#CCCCCC';

                if (selectedMetric === 'Readiness' && readinessLookup[countryCode]) {
                  fillColor = getReadinessColor(readinessLookup[countryCode]);
                } else if (selectedMetric === 'Bills' && billsLookup[countryCode] !== undefined) {
                  fillColor = getBillsColor(billsLookup[countryCode]);
                } else if (selectedMetric === 'Investment' && investmentLookup[countryCode]) {
                  fillColor = getInvestmentColor(investmentLookup[countryCode]);
                }

                return {
                  fillColor,
                  weight: 1,
                  opacity: 1,
                  color: 'white',
                  fillOpacity: 0.7
                };
              },
              onEachFeature: (feature, layer) => {
                const countryCode = feature.properties.iso_a3;
                const readiness = readinessLookup[countryCode] !== undefined ? 
                  readinessLookup[countryCode].toFixed(2) : 'No data';
                const bills = billsLookup[countryCode] !== undefined ? 
                  billsLookup[countryCode] : 'No data';
                const investment = investmentLookup[countryCode] !== undefined ? 
                  `$${investmentLookup[countryCode].toLocaleString()}M` : 'No data';

                layer.bindPopup(`
                  <strong>${feature.properties.name}</strong><br>
                  AI Readiness Score: ${readiness}<br>
                  Legislative Mentions: ${bills}<br>
                  AI Investment: ${investment}
                `);
              }
            }).addTo(map);

            // Add population overlay if enabled
            if (showPopulation) {
              populationData.forEach(country => {
                const coords = countryCoordinates[country.iso_a3];
                if (coords && country.population) {
                  const radius = getPopulationRadius(country.population);
                  if (radius > 0) {
                    L.circleMarker(coords, {
                      radius,
                      fillColor: '#FF4136',
                      color: '#85144b',
                      weight: 1,
                      opacity: 0.8,
                      fillOpacity: 0.35
                    }).bindPopup(`
                      <strong>${country.countryName}</strong><br>
                      Population: ${country.population}M
                    `).addTo(map);
                  }
                }
              });
            }

            // Force a resize event after the map is fully loaded
            setTimeout(() => {
              map.invalidateSize();
            }, 100);
          })
          .catch(error => {
            console.error('Error loading GeoJSON:', error);
          });
      } catch (error) {
        console.error('Error initializing map:', error);
      }
    }, 100);

    // Cleanup function
    return () => {
      clearTimeout(initMap);
      if (map) {
        map.remove();
      }
    };
  }, [selectedMetric, showPopulation]);

  return (
    <Box bg={bgColor} minH="100vh" py={8}>
      <Container maxW="container.xl">
        <Tabs isLazy variant="enclosed" colorScheme="blue">
          <TabList mb="1em" overflowX="auto" flexWrap="nowrap">
            <Tab>Homepage</Tab>
            <Tab>Why Global Inclusion Matters</Tab>
            <Tab>Barriers to Inclusive Red Teaming</Tab>
            <Tab>Enabling Participation</Tab>
            <Tab>Domain-Specific Governance</Tab>
            <Tab>Global Coordination</Tab>
            <Tab>References and Resources</Tab>
          </TabList>

          <TabPanels>
            {/* Homepage Tab */}
            <TabPanel>
              <VStack spacing={6} align="stretch">
                <Heading as="h2" size="xl" mb={4}>
                  Bridging the Global AI Divide: Towards Inclusive Safety
                </Heading>

                {/* Map Controls */}
                <Box mb={4}>
                  <HStack spacing={8} mb={4}>
                    <RadioGroup value={selectedMetric} onChange={setSelectedMetric}>
                      <Stack direction="row">
                        <Radio value="Readiness">AI Readiness</Radio>
                        <Radio value="Investment">AI Investment</Radio>
                        <Radio value="Bills">Legislative Mentions</Radio>
                      </Stack>
                    </RadioGroup>
                    <Checkbox
                      isChecked={showPopulation}
                      onChange={(e) => setShowPopulation(e.target.checked)}
                    >
                      Show Population Overlay
                    </Checkbox>
                  </HStack>
                </Box>

                {/* Map Container */}
                <Box
                  ref={mapRef}
                  h="500px"
                  w="561.516px"
                  position="relative"
                  borderRadius="lg"
                  overflow="hidden"
                  border="1px solid"
                  borderColor={borderColor}
                  mb={4}
                  mx="auto"
                  sx={{
                    '.leaflet-container': {
                      width: '100%',
                      height: '100%'
                    }
                  }}
                />

                {/* Legend */}
                <Box p={4} bg={useColorModeValue('gray.50', 'gray.700')} borderRadius="lg">
                  <Heading as="h3" size="sm" mb={2}>Legend</Heading>
                  <HStack spacing={4}>
                    <Box>
                      <Box w="20px" h="20px" bg="#ff4444" display="inline-block" mr={2} />
                      {selectedMetric === 'Readiness' ? 'Low Readiness' : 
                       selectedMetric === 'Investment' ? 'Low Investment' : 'Few Mentions'}
                    </Box>
                    <Box>
                      <Box w="20px" h="20px" bg="#ffbb33" display="inline-block" mr={2} />
                      {selectedMetric === 'Readiness' ? 'Medium Readiness' : 
                       selectedMetric === 'Investment' ? 'Medium Investment' : 'Some Mentions'}
                    </Box>
                    <Box>
                      <Box w="20px" h="20px" bg="#00C851" display="inline-block" mr={2} />
                      {selectedMetric === 'Readiness' ? 'High Readiness' : 
                       selectedMetric === 'Investment' ? 'High Investment' : 'Many Mentions'}
                    </Box>
                    <Box>
                      <Box w="20px" h="20px" bg="#cccccc" display="inline-block" mr={2} />
                      No Data
                    </Box>
                    {showPopulation && (
                      <Box>
                        <Box
                          w="20px"
                          h="20px"
                          borderRadius="full"
                          bg="#4A90E2"
                          opacity={0.4}
                          border="1px solid #2171C7"
                          display="inline-block"
                          mr={2}
                        />
                        Population
                      </Box>
                    )}
                  </HStack>
                </Box>

                <Text mb={3}>
                  Artificial intelligence is advancing at breakneck speed, yet its development and benefits are dramatically concentrated. While AI innovation flourishes primarily in a few Global North countries, its impacts reverberate globally, touching populations who often have little influence over its design or deployment.
                </Text>
                <Text mb={3}>
                  This disconnect creates a stark reality: projections estimate that by 2030, North America, Europe, and China will capture <Link href="https://www.pwc.com/gx/en/issues/analytics/assets/pwc-ai-analysis-sizing-the-prize-report.pdf" isExternal color="blue.500">84% of AI's economic gains</Link>, while regions like Africa, Oceania, and the rest of Asia combined may see only 8%. Such trajectories threaten to <Link href="https://foreignpolicy.com/2024/12/17/ai-global-south-inequality/" isExternal color="blue.500">deepen global inequality</Link>, as leading AI nations gain outsized advantages. Furthermore, without inclusive governance, dominant cultural and economic perspectives risk imposing 'one-size-fits-all' AI values and policies ill-suited to diverse local contexts.
                </Text>
                <Text mb={3}>
                  The data often used to train AI reflects this imbalance. Large language models, typically trained on internet data, <Link href="https://www.weforum.org/stories/2024/09/ai-linguistic-diversity-gap-missed-opportunity/" isExternal color="blue.500">underrepresent the world's linguistic diversity; none of the top 34 internet languages are African, and most models cover only about 100 of over 7,000 global languages</Link>. This occurs despite Africa being home to nearly 19% of the world's population. Consequently, even advanced AI models exhibit <Link href="https://ar5iv.org/pdf/2402.02680" isExternal color="blue.500">geographic biases</Link> favoring high-income regions and can perpetuate harmful stereotypes, such as rating African residents less attractive than Europeans.
                </Text>
                <Text mb={3}>
                  The stakes are high. This isn't merely about representation in development teams; it's about the real-world consequences when widely used AI systems reflect narrow perspectives. Models trained primarily on Western data can malfunction or misdiagnose when applied elsewhere – for instance, a health AI accurate for European patients <Link href="https://link.springer.com/book/10.1007/978-3-031-08215-3" isExternal color="blue.500">has shown significant errors when used with African patients</Link>, highlighting how data and design choices impact lives.
                </Text>
                <Text mb={3}>
                  This project confronts this governance gap by focusing on a critical lever for more democratic AI oversight: AI red teaming. By deliberately probing AI systems for vulnerabilities, biases, and unexpected failure modes, we can uncover risks missed by internal teams. Our goal is to identify pathways toward inclusive AI security governance – frameworks that embed global perspectives and promote equity, safety, and accountability across all linguistic and geographic divides.
                </Text>
                <Text as="i" mt={4}>
                  For detailed sources and further reading, please see the References and Resources section.
                </Text>
              </VStack>
            </TabPanel>

            {/* Why Global Inclusion Matters Tab */}
            <TabPanel>
              <VStack spacing={6} align="stretch">
                <Heading as="h2" size="xl">
                  Why Global Inclusion Matters
                </Heading>

                <Box>
                  <Heading as="h3" size="md" mb={3}>
                    Linguistic & Geographic Bias
                  </Heading>
                  <Text>
                    AI systems often perform poorly for languages or communities absent in training data. Independent researchers have found that large language models (trained mostly on English and European data) carry "geographic bias" – for instance, systematically underrating content related to African countries on subjective qualities like "intelligence" or "morality".
                  </Text>
                  <Text mt={3}>
                    Generative AI tools also struggle with many low-resource languages, leading to errors and exclusion of non-English speakers. These biases can amplify inequality (e.g. an AI assistant that works well in English but garbles Swahili is far less useful to African users).
                  </Text>
                </Box>

                <Box>
                  <Heading as="h3" size="md" mb={3}>
                    Health & Safety Risks
                  </Heading>
                  <Text>
                    If AI applications (like medical diagnosis tools) are developed using homogenous data, they can be dangerously unreliable for underrepresented groups. For example, an AI system for skin cancer screening was found to be less accurate on patients with darker skin, because most training images were of light-skinned individuals.
                  </Text>
                </Box>

                <Box>
                  <Heading as="h3" size="md" mb={3}>
                    Cultural Mismatches & Fairness
                  </Heading>
                  <Text>
                    AI systems often reflect the cultural context of their creators, which can cause problems when deployed globally. Generative AI models have been caught reinforcing stereotypes – for instance, a text-to-image generator trained on Western data might depict Indian festivals in an exotified, caricatured way.
                  </Text>
                </Box>
              </VStack>
            </TabPanel>

